santaVisits = santaVisits_darvs

# A second Ruby version
# Ahhhhh... the each_with_index function is an analog to the enumerate()
# Python function I used. So now I get the index too, so I don't have
# to worry about updating it myself.
#
# So that's really close to my Python version
#

floor = 0

santaVisits.chars.each_with_index { |c, index|
	if c == '('
		floor = floor + 1
	elsif c == ')'
		floor = floor - 1
	end

	if floor == -1
		puts index+1
		# break is used to get out of the for loop once we're done
		break
	end
}
