#!/usr/bin/env ruby

class Position
  @x = 0
  @y = 0

  attr_reader :x, :y

  def initialize(x, y)
    @x = x
    @y = y
  end

  def move(direction)
    case direction
      when 'D'
        @y = [@y + 1, 2].min
      when 'U'
        @y = [@y - 1, 0].max
      when 'R'
        @x = [@x + 1, 2].min
      when 'L'
        @x = [@x - 1, 0].max
    end
  end

  def to_s
    return ((@y*3)+@x+1).to_s
  end
end

def AoC1602(input)

  # Our telephone keypad is like this:
  #
  #    1 2 3
  #    4 5 6
  #    7 8 9
  #
  # The 5 key would be the 2nd in X and 2nd in Y.
  # Counting from 0,0 at the top left, the 5 key would be (1,1)
  #
  position = Position.new(1,1)
  number = ''

  puts("Starting Position #{position}")

  input.each do |sequence|

    sequence.each_char do |key|
      position.move(key)
      puts("[#{key}] -> #{position}")
    end

    puts("KEY PRESS #{position}")
    number = number + "#{position}"

  end

  puts("\nNumber is #{number}")

end


# AoC1602(["ULL", "RRDDD", "LURDL", "UUUUD"])

AoC1602([
'',
'',
