#!/usr/bin/env ruby

class Position
  @x = 0
  @y = 0

  @keypad = []
  @num_lines = 0
  @num_columns = 0

  def initialize(keypad, start)
    @keypad = keypad

    @num_lines = @keypad.length
    @num_columns = @keypad[0].length

    find(start)
  end

  def find(key)
    puts("KEYPAD: #{@keypad}")

    @keypad.each_with_index do |line, line_number|
      line.each_char.with_index do |k, key_number|
        if k == key
          @x = key_number
          @y = line_number

          return
        end
      end
    end
  end


  def move(direction)
    new_x = @x
    new_y = @y
    case direction
      when 'D'
        new_y = [@y + 1, @num_lines-1].min
      when 'U'
        new_y = [@y - 1, 0].max
      when 'R'
        new_x = [@x + 1, @num_columns-1].min
      when 'L'
        new_x = [@x - 1, 0].max
    end

    # Check if we would be moving on a SPACE where there is no key

    if @keypad[new_y][new_x] != ' '
      @x=new_x
      @y=new_y
    end

  end

  def to_s
    return @keypad[@y][@x]
  end
end

def AoC1602(keypad, start, input)

  # Our telephone keypad is like this:
  #
  #    1 2 3
  #    4 5 6
  #    7 8 9
  #
  # The 5 key would be the 2nd in X and 2nd in Y.
  # Counting from 0,0 at the top left, the 5 key would be (1,1)
  #
  position = Position.new(keypad, start)
  number = ''

  puts("Starting Position #{position}")

  input.each do |sequence|

    sequence.each_char do |key|
      position.move(key)
      puts("[#{key}] -> #{position}")
    end

    puts("KEY PRESS #{position}")
    number = number + "#{position}"

  end

  puts("\nNumber is #{number}")
  puts("----------------------------------")

end

